<?php include("../php/connect.php");$count = $_POST['count'];if(isset($count)){	$connection = connect() or die("Failed to connect:".mysql_error());	$rows = mysql_query_or_die(		"select 			p.Name, s.Score, s.Time, s.Won		from 			Player p 				inner join Score s					on p.Id = s.PlayerId		order by 			s.Score desc		limit			0, ".mysql_real_escape_string($count), $connection);		$result = array();	while($row = mysql_fetch_assoc($rows)){		array_push($result, $row);	}	echo json_encode($result);} else {	param_not_set("count");}?>